#include <bits/stdc++.h>
using namespace std;
using LL = long long;
LL gcd(LL a, LL b) {
    return b == 0 ? a : gcd(b, a % b);
}
const int INF = 0x3f3f3f3f;
const LL INFL = 0x3f3f3f3f3f3f3f3f;
const int mod = 1e9 + 7;
//-------------------end head--------------
char s[1010] = "";

int main() {
    int n = strlen(s);
    long long ans = 0;
    for (int i = 0; i + 12 < n; ++i) {
        long long temp = 1;
        for (int j = i; j < i + 13; ++j)
            temp *= s[j] - '0';
        ans = max(ans, temp);
    }
    cout << ans << endl;
    return 0;
}
